import React from 'react';
import styles from './Card.module.css';

function Card({ post }) {
  const regex = /\s/g;
  const URL_title = post.title.replace(regex, '-').toLowerCase();

  return (
    <div>
      <Link href={`/article/${URL_title}`}>
        <div className={styles.feed_card_inner}>
          <img
            src='https://uploads.guim.co.uk/2020/04/04/daniel-sessler-N98aPtXcDYs-unsplash.jpg'
            className='card_img'
          ></img>
          <div className={styles.card_title}>{post.title}</div>

          {post.tags.length > 0 && (
            <div className={styles.card_tags}>
              {post.tags[0] && (
                <div className={styles.tag}>{'#' + post.tags[0].name}</div>
              )}
              {post.tags[1] && (
                <div className={styles.tag}>
                  {post.tags[1] && '#' + post.tags[1].name}
                </div>
              )}
              {post.tags[2] && (
                <div className={styles.tag}>
                  {post.tags[2] && '#' + post.tags[2].name}
                </div>
              )}
            </div>
          )}

          <div className={styles.card_line}>
            <div className={styles.card_author}>{post.author} </div>
            <div className={styles.card_timestamp}>
              <div>
                {post.timestamp}
              </div>
            </div>
          </div>
        </div>
        <div className='card_bottom'>
          <div className='card_votes'>â™¡ {post.votes}</div>
          <div className='card_btn'>Save</div>
        </div>
      </Link>
    </div>
  );
}

export default Card;
