import React, { useState } from 'react';
import Card from '../Card/Card';
import styles from './Feed.module.css';
import FeedHeader from './FeedHeader';

function Feed({
  posts,
  feedState,
  addToList,
  deleteFromList,
  checkReadingList
}) {

 // const [list, setList] = useState();


  const sortBy = (posts, method) => {
    if (method === 'popular') {
      const sortedList = posts.sort(function(a, b) {
        a = a.votes;
        b = b.votes;
        return a > b ? -1 : a < b ? 1 : 0;
      });
    }

    if (method === 'latest') {
      const sortedList = posts.sort(function(a, b) {
        a = a.timestamp;
        b = b.timestamp;
        return a > b ? -1 : a < b ? 1 : 0;
      });
    }
    return sortedList;
  };



  return (
    <div className={styles.feed_body}>
      {feedState === 'tags' && <FeedHeader filter={'tags'} />}
      {feedState === 'search' && <FeedHeader filter={'search'} />}
      {feedState === 'list' && <FeedHeader filter={'list'} />}
      {feedState === 'normal' && (
        <FeedHeader filter={'normal'} sortBy={sortBy} />
      )}

      {sortBy(posts, 'popular').map((post, i) => (
        <div className={styles.feed_card} key={post._id}>
          <Card
            id={i}
            post={post}
            addToList={addToList}
            feedState={feedState}
            deleteFromList={deleteFromList}
            checkReadingList={checkReadingList}
          />
        </div>
      ))}
    </div>
  );
}

export default Feed;
