import React from 'react';
import Link from 'next/link'
import styles from './NavBar.module.css';
import { useAuth, withAuth } from 'use-auth0-hooks';
import { useRouter } from 'next/router';



function NavBar({auth}) {
  const { pathname, query } = useRouter();
  const { isLoading, login, logout, isAuthenticated } = useAuth();
  const { user } = auth;

  return (
    <div className={styles.nav_wrapper}>
      <div className={styles.navbar}>
      <Link href="/"><a>  <img className={styles.logo_png} src="https://uploads.guim.co.uk/2020/04/15/culture.png"></img></a></Link>

        <div className={styles.navbar_logo}><Link href="/"><a>       
 Stagetime</a></Link></div>


      { isAuthenticated  &&
        <div className={styles.navbar_write}>

          <Link href="/write"><a>Write a post</a></Link></div>}

          { !isAuthenticated  &&
        <div className={styles.navbar_write}>
          <div onClick={() => login({ appState: { returnTo: '/write' } })}>Write a post</div></div>}    
      </div>
    </div>
  );
}

export default withAuth(NavBar);
