  dataLength={8} //This is important field to render the next data
  next={posts}
  hasMore={true}
  loader={<h4>Loading...</h4>}
  endMessage={
    <p style={{textAlign: 'center'}}>
      <b>Yay! You have seen it all</b>
    </p>
  }>



      {sortedList.slice(0,numPosts).map(post => (
        <div className={styles.feed_card}>
          <Card
            key={post.id}
            post={post}
            addToList={addToList}
            feedState={feedState}
            deleteFromList={deleteFromList}
            checkReadingList={checkReadingList}
          />
        </div>

      ))}
      </InfiniteScroll>

    </div>
  );
}

export default Feed;
